mapper.select:
SELECT * FROM ORDER_SHEET
WHERE 1=1
and ORDER_DT >= #{start}
and ORDER_DT <= #{end}
ORDER BY ORDER_NO

SELECT * FROM ORDER_SHEET

WHERE 1=1
and VEND_NM = #{vendNm}
and PRDT_NM = #{prdtNm}
and ORDER_DT >= #{start}
and ORDER_DT <= #{end}
ORDER BY ORDER_NO

SELECT i.edcts_ist_no, i.edcts_ist_dt, i.edcts_ist_cnt, i.edcts_cd,
i.edcts_lot_no, e.prdt_nm
FROM edcts_ist i JOIN edcts e
ON i.edcts_cd =
e.edcts_cd
and EDCTS_LOT_NO = #{edctsLotNo}
and EDCTS_IST_DT >= #{edctsIstDtStart}
and EDCTS_IST_DT <= #{edctsIstDtEnd}
ORDER BY edcts_ist_no

mapper.update:
UPDATE ORDER_SHEET
SET PROG_APPE = 'Completed'
WHERE ORDER_NO = #{orderNo}
